mapper.select:
SELECT o.id as ordersId, o.`no`, o.`status`, o.arrival_time, o.tail_money, o.total_money, o.create_date, o.assemble_seats, o.number, m.`name` as merchantName, mg.`name` as goodsName, m.address, m.logo FROM ds_orders o LEFT JOIN ds_merchant m ON o.seller = m.id LEFT JOIN ds_merchant_goods mg ON o.goods_id = mg.id WHERE o.buyer = #{userId}
AND o.status IN (${status.key})
ORDER BY o.update_date DESC, o.create_date DESC LIMIT #{offset},#{limit}

SELECT o.id as ordersId, m.name as merchantName, o.assemble_seats_count, o.assemble_seats_surplus FROM ds_orders o LEFT JOIN ds_merchant m ON o.seller = m.id WHERE o.assemble_seats = ${_BOOLEAN_STATUS_YES} AND o.assemble_seats_surplus > 0 AND o.status=2
AND m.name LIKE CONCAT("%",CONCAT(#{merchantName},"%"))
ORDER BY o.create_date DESC LIMIT #{offset},#{limit}
